let numbers = [20000,30000,40000,50000,12000,13000,14000];

function biggestPrice(numbers){
    // console.log(numbers);
    let biggerPrice = numbers[0];
    for(const number of numbers){
        // console.log(number);
        if(number > biggerPrice){
            biggerPrice = number;        }
    }
    return biggerPrice;
}


const bigger = biggestPrice(numbers);
console.log(bigger);


